{% block title %}NouvelleMesure liste{% endblock %}

{% block body %}
    <h1>NouvelleMesure liste</h1>

    <table class=\"table table-striped table-bordered \">
        <thead>
            <tr>
                <th>Id</th>
                <th>Montant</th>
                <th>Date</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for nouvelle_mesure in nouvelle_mesures %}
            <tr>
                <td>{{ nouvelle_mesure.id }}</td>
                <td>{{ nouvelle_mesure.montant }}</td>
                <td>{{ nouvelle_mesure.date ? nouvelle_mesure.date|date('d-m-Y') : '' }}</td>
                <td>
                    <div class=\"btn-group\" role=\"group\" aria-label=\"Basic example\">
                        <button type=\"button\" class=\"btn btn-secondary\"> <a href=\"{{ path('nouvelle_mesure_show', {'id': nouvelle_mesure.id}) }}\">Voir <i class=\"fa fa-eye\"
                            aria-hidden=\"true\"></i></a></button>
                        <button type=\"button\" class=\"btn btn-secondary\"><a href=\"{{ path('nouvelle_mesure_edit', {'id': nouvelle_mesure.id}) }}\">editer <i class=\"fa fa-pencil\"
                            aria-hidden=\"true\"></i></a></button>
                      </div>
                   
                    
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"4\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
    <button type=\"button\" class=\"btn btn-secondary\">    <a href=\"{{ path('nouvelle_mesure_new') }}\">Créer un nouveau </a>
    </button>

{% endblock %}
